import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const store = new Vuex.Store({
    state: {
        currentPathName: ''
    },
    mutations: {
        setPath(state){
            state.currentPathName = localStorage.getItem("currentPathName")
        },
        resetUser(){
            localStorage.removeItem("user")
        }
    }
})

export default store
