import request from "@/utils/request";
export default {
  name: "Person",
  data() {
    return {
      form: {}
    };
  },
  methods: {
    save() {
      request.post("http://localhost:8080/user", this.form).then(res => {
        if (res) {
          this.$message.success("保存成功");
        } else {
          this.$message.error("保存失败");
        }
      });
    }
  }
};
