import Aside from "@/components/Aside.vue";
import Header from "@/components/Header.vue";
import request from "@/utils/request";
export default {
  name: 'HomeView',
  data() {
    return {
      collapseBtnClass: 'el-icon-s-fold',
      isCollapse: false,
      sideWidth: 200,
      logoTextShow: true,
      user: {}
    };
  },
  components: {
    Aside,
    Header
  },
  created() {
    // 从后台获取最新的
    this.getUser();
  },
  methods: {
    collapse() {
      //点击收缩按钮触发收缩
      this.isCollapse = !this.isCollapse;
      if (this.isCollapse) {
        this.sideWidth = 64;
        this.collapseBtnClass = 'el-icon-s-unfold';
        this.logoTextShow = false;
      } else {
        //展开
        this.sideWidth = 200;
        this.collapseBtnClass = 'el-icon-s-fold';
        this.logoTextShow = true;
      }
    },
    getUser() {
      // 从后台获取数据
      request.get("http://localhost:8080/user/username/" + this.user.username).then(res => {
        // 重新赋值最新user数据
        this.user = res.data;
      });
    }
  }
};
